def Encryption( text , shift):
    cypher_text = ""
    for char in text:
        position = alphabet.index(char)
        new_position = position + shift
        cypher_text = cypher_text + alphabet[new_position]
    print(f"This is your cypher text:{cypher_text}")



def Decryption( cypher_text, shift):
    Plain_text = ""
    for char in cypher_text:
        position = alphabet.index(char)
        new_position = position - shift
        Plain_text = Plain_text + alphabet[new_position]
    print(f"This is your plain text:{Plain_text}")

